import {People} from './People';

const Card =(props) => { // standart bir syntax, eğer component(props) kullanacaksan ilgili component class'ından extend ediyoruz.
	// Componenttan extend edilen classlarda render() kullanmak bi zorunlulukmuş.
		return(
		<div className='shadow-2 dib b--yellow br3 bg-yellow grow justify-content:center ma2'>
		<img alt='robots 'src='https://robohash.org/kerim'/>
		<h2><center>{People[0].name}</center></h2>
		<p>{props.detay}</p>
		<p><center>{People[0].Email}</center></p>
		</div>
		);



}

export default Card;
