import React, {Component} from 'react';
import CardList from './CardList';
import {People} from './People';
import SearchBox from './SearchBox';


class App extends Component {
	constructor(){
		super();
		this.state={ //burada this.state yaparken state'i deklere etmeden nas覺l kulland覺?
			People:People,
			searchfield:''
		}
	}

	onSearchChange=(event)=>{

		const person=this.state.People.filter(person=>{
			return person.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})
		console.log(person);
	}

	render(){
		return( 
		<div className='tc'>
			<h1 >Robotlar</h1>
			<SearchBox searchChange={this.onSearchChange}/>
      		<CardList people={this.state.People}/>
    	</div>
		);

	}

}

export default App;
