	// Componenttan extend edilen classlarda render() kullanmak bi zorunlulukmuş.
		return(
		<div className='tc shadow-2 dib b--yellow br3 bg-yellow grow justify-content:center ma2'>
		<img alt='robots' src={`https://robohash.org/kerim`}/>
		<h2>{People[0].name}</h2>
		<p>{People[0].Email}</p>
		</div>
		);



}

export default Card;
